var navBg=document.getElementById("nav-bg"),nav=document.getElementsByTagName("NAV"),header=document.getElementsByTagName("HEADER"),i;function openMenu(){navBg.classList.add("nav-open");for(i=0;i<nav.length;i++)nav[i].classList.add("nav-open")}function closeMenu(){for(i=0;i<nav.length;i++)nav[i].classList.remove("nav-open");navBg.classList.remove("nav-open")}navBg.addEventListener("click",closeMenu);
function handleScroll(){for(i=0;i<header.length;i++)header[i].classList.toggle("header-shadow",0<window.pageYOffset)}document.addEventListener("scroll",handleScroll);
